agetitle["view"]=$params["viewtitle"];			if(isset($params["message"]))		$grid->message=$params["message"];				if(isset($params["height"]))		$grid->height=$params["height"];	if(isset($params["width"]))		$grid->width=$params["width"];				if(isset($params["checkbox"]))		$grid->data=true;	else		$grid->data=false;		if(isset($params["addmethod"])){		$add=true;		$grid->addmethod=$params["addmethod"];	}	else		$add=false;			if(isset($params["viewmethod"])){		$view=true;		$grid->viewmethod=$params["viewmethod"];	}	else		$view=false;			if(isset($params["editmethod"])){		$edit=true;		$grid->editmethod=$params["editmethod"];	}	else		$edit=false;			if(isset($params["approvemethod"])){		$approve=true;		$grid->approvemethod=$params["approvemethod"];	}	else		$approve=false;			if(isset($params["settingmethod"])){		$setting=true;		$grid->settingmethod=$params["settingmethod"];	}	else		$setting=false;		if(isset($params["deletemethod"])){		$delete=true;		$grid->deletemethod=$params["deletemethod"];	}	else		$delete=false;	if(isset($params["rowcss"]))		$grid->rcss=$params["rowcss"];	if(isset($params["altrowcss"]))		$grid->altcss=$params["altrowcss"];	if(isset($params["datarows"]))		$grid->datarows=$params["datarows"];	if(isset($params["datacols"]))		$grid->datacols=$params["datacols"];	if(isset($params["idenable"]))		$grid->tridenable=$params["idenable"];	if(isset($params["ajax"]))		$grid->tridenable=$params["ajax"];	if(isset($params["checkbox"]))		$grid->checkbox=$params["checkbox"];	$html=$grid->preparehtml();	//$html=$grid->getHTML();	return $html;}?>