<?phprequire_once("form.class.php");require_once("input.class.php");function smarty_function_theme($params, &$smarty){	}function smarty_block_Form($params, $content, &$smarty, &$repeat){	$str="Form".THEME;	$frm = new $str();	foreach($params as $key=>$val)	{		if(is_array($val))		{			if(isset($val) && count($val)>0)				$frm->$key=$val;		}		else		{			if(isset($val) && strlen($val)>0)				$frm->$key=$val;		}	}		$str=$frm->pre_form();	if(!$repeat){        if (isset($content)) {			$str.= $content;        }    }	$str.=$frm->post_form();	return $str;}function smarty_function_input($params, &$smarty){	$str="Input".THEME;	$input = new $str();	foreach($params as $key=>$val)	{		if(is_array($val))		{			if(isset($val) && count($val)>0)				$input->$key=$val;		}		else		{			if(isset($val) && strlen($val)>0)				$input->$key=$val;		}	}	$func=$params["type"];	$ctrl=$input->$func();	$str=$input->preparehtml($ctrl);	return $str;}function smarty_Input($params){	$str="Input".THEME;	$input = new $str();	foreach($params as $key=>$val)	{		if(is_array($val))		{			if(isset($val) && count($val)>0)				$input->$key=$val;		}		else		{			if(isset($val) && strlen($val)>0)				$input->$key=$val;		}	}	return $input;}function smarty_function_text($params, &$smarty){	$input = smarty_Input($params);	$input->type="text";	$ctrl=$input->text();	$str=$input->preparehtml($ctrl);	return $str;}function smarty_function_hidden($params, &$smarty){	$input = smarty_Input($params);	$input->type="hidden";	$ctrl=$input->hidden();	$str=$input->preparehtml($ctrl);	return $str;}function smarty_function_htmledit($params, &$smarty){	$input = smarty_Input($params);	$input->type="htmledit";	$ctrl=$input->htmledit();	$str=$input->preparehtml($ctrl);	return $str;}function smarty_function_number($params, &$smarty){	$input = smarty_Input($params);	$input->type="number";	$ctrl=$input->number();	$str=$input->preparehtml($ctrl