<?phprequire_once("wizard.class.php");function smarty_block_wizard($params, $content, &$smarty, &$repeat){	$str1="Sticker".THEME;	$stk = new $str1();	foreach($params as $key=>$val)	{		if(isset($val) && strlen($val)>0)			$stk->$key=$val;	}	$str="<div class=\"row-fluid\">";	if(!$repeat){        if (isset($content)) {			$str.= $content;        }    }	$str.="</div>";	return $str;}function smarty_function_wizard_data($params, &$smarty){	$str="Sticker".THEME;	$stk = new $str();	foreach($params as $key=>$val)	{		if(isset($val) && strlen($val)>0)			$stk->$key=$val;	}	$str=$stk->preparehtml();	return $str;}?>