<?phpabstract class Sticker{	protected $data;	protected $counter;	protected $logger;	public function __set($key, $val)	{		$key=strtolower($key);		$this->data[$key]=$val;		$this->logger = logger::getRootlogger();	}	public function __get($key)	{		$key=strtolower($key);		return $this->data[$key];	}	public abstract function preparehtml();	}class StickerMetro extends Sticker{	private $html;	public function __construct($session, $module, $keyfield="")	{		$this->data["session"]=$session;		$this->data["module"]=$module;		$this->data["keyfield"]=$keyfield;		$this->html="";	}		private function prepareURL()	{		$_url="";		if(isset($this->data["session"]))			$_url.="session=".$this->data["session"]."&";		if(isset($this->data["module"]))			$_url.="module=".encode($this->data["module"])."&";				return $_url;	}		public function preparehtml()	{		$url=$this->prepareURL();		$sw=implode($this->data["data"],',');		$str.="<div class=\"span3 statbox ".$this->data["color"]."\" onTablet=\"span6\" onDesktop=\"span3\">\n";		$str.="\t<div class=\"boxchart\">".$sw."</div>";		$str.="\t<div class=\"number\">".$this->data["number"]."<i class=\"icon-arrow-".$this->data["arrow"]."\"></i></div>";		$str.="\t<div class=\"title\">".$this->data["title"]."</div>";		$str.="\t<div class=\"footer\">";		$str.="\t\t<a href=\"index.php?".$url."action=".encode($this->data["href_action"])."\">".$this->data["link"]."</a>";		$str.="\t</div>";			$str.="</div>";		return $str; 	}	}?>